package com.aggarwal.home;

// Strings in java

public class StringsJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String apple = "Apple";
		
		// Prints the Value of apple
		System.out.println(apple);
		
		//Prints the length apple variable
		System.out.println(apple.length());
		
		//Return True or False for Comparison
		
		System.out.println(apple.equals("Apple"));
		
		
		//Concatenation of String√è
		System.out.println(apple.concat(" and Mango"));
		
	}

}